    public String getRandomAnecdote(){
        int pos = getRandomNumberAnecdote();
        System.out.println(arrayAnecdotes.get(pos));
        return arrayAnecdotes.get(pos);
    }

    public int getRandomNumberAnecdote(){
        int pos = random.nextInt(arrayAnecdotes.toArray().length);
        return pos;
    }
